import React from "react";
import DeleteIcon from '@material-ui/icons/Delete';

const Note = (props) => {

    const deleteNote = () => {
        props.deleteItem();
    }

    return (
        <>
           <div className="note">
                <h1> {props.title} </h1>
                <br />
                <p> {props.content} </p>
                <button className="btn" onClick={deleteNote}>
                    <DeleteIcon className="deleteIcons"/>
                </button>
           </div>
        </>
    );
};

export default Note
